pragma solidity 0.8.30;

interface IBasePaymentVerifier {
    /* ============ Events ============ */
    event CurrencyAdded(bytes32 indexed currencyCode);
    event CurrencyRemoved(bytes32 indexed currencyCode);
    event TimestampBufferSet(uint256 timestampBuffer);

    /* ============ Functions ============ */
